
public class Ejercicio1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**
		 * Declara dos variables númericas (con el valor que desees), muestra por consola la suma, resta, multiplicación, división y modulo.
		 */
		short a=20;
		short b=15; 
		int suma=a+b;
		int resta=a-b;
		int multiplicación=a*b;
		int división=a/b;
		int módulo=a%b;
		System.out.println("suma: " + suma);
		System.out.println("resta: " + resta);
		System.out.println("multiplicación: " + multiplicación);
		System.out.println("división: " + división);
		System.out.println("módulo: " + módulo);
		
		
	}

}
