
public class Ejercicio2App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/** Escribe un programa Java que realice lo siguiente: declarar una variable N de tipo int, 
		 * una varaible A de tipo double y una variable C de tipo char y asigna a cada una un valor. 
		 * A continuación muestra por pantalla diferentes acciones
		 */
		int N=13;
		double A=5.68;
		char C='X'; 
		double suma=N+A;
		double resta=N-A;
		int valorC=(int)C; 
		System.out.println("Variable N: "+N);
		System.out.println("Variable A: "+A);
		System.out.println("Variable C: "+C);
		System.out.println("suma: "+suma);
		System.out.println("resta: "+resta);
		System.out.println("Valor del número del carácter X: "+valorC);
				
	}

}
