
public class Ejercicio1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**
		 * Declara dos variables númericas (con el valor que desees), muestra por consola la suma, resta, multiplicación, división y modulo.
		 */
		short a=20;
		short b=15; 
		int suma=a+b;
		int resta=a-b;
		int multiplicación=a*b;
		int división=a/b;
		int módulo=a%b;
		
		System.out.println("Suma: "+suma +
				"\n" +"Resta: "+resta +
				"\n" +"Multiplicación: "+multiplicación +
				"\n" + "División: "+división +
				"\n" + "Módulo: "+módulo); 
		 
	}

}
